import React, {useState} from 'react';
import Tab1 from './Tab1';
import Tab2 from './Tab2';

const Tab = () => {
    const tabTitle = ['tab1','tab2'];
    const tab = { 1 : <Tab1 />, 2 : <Tab2/>}
    const [tabMenu, setTabMenu] = useState(1);
    const onTab = (idx) => {
        setTabMenu(idx);
    };

    return (
        <div>
            <div>
                {tabTitle.map((title, idx) => {
                    return (
                        <span key={idx} onClick={() => onTab(idx)}>{title}</span>
                    );
                })}
            </div>
            <div>
                {tab[tabMenu]}
            </div>
        </div>
    );
};

export default Tab;
